var addToBasketBtns=document.querySelectorAll('.product-hidden .buy');var popupAddToBasket=document.querySelector('.modal-added-cart');var modalCloseBtn=document.querySelector('.modal-close');var continueShoppingBtn=document.querySelector('.continue-shopping');for(var i=0;i<addToBasketBtns.length;i++){addToBasketBtns[i].addEventListener('click',function(evt){evt.preventDefault();popupAddToBasket.classList.add('modal-show')})}
modalCloseBtn.addEventListener('click',function(evt){evt.preventDefault();popupAddToBasket.classList.remove('modal-show')});continueShoppingBtn.addEventListener('click',function(evt){evt.preventDefault();popupAddToBasket.classList.remove('modal-show')});window.addEventListener('keydown',function(evt){if(evt.keyCode===27){evt.preventDefault();if(popupAddToBasket.classList.contains('modal-show')){popupAddToBasket.classList.remove('modal-show')}}})